  const imageList = [
    require("../../../assets/image1.jpeg"),
    require("../../../assets/image2.jpeg"),
    require("../../../assets/image1.jpeg"),
    require("../../../assets/image2.jpeg"),
  ];
  const scrollX = useRef(new Animated.Value(0)).current;
  const [currentIndex, setCurrentIndex] = useState(0);
  const slidesRef = useRef(null);

